import React, { useRef, useState, useEffect } from 'react';
import '../styles/PropertyListings.css'; // Make sure to update this path to your new CSS file if needed
import { Link } from 'react-router-dom';
import { FiChevronLeft, FiChevronRight } from 'react-icons/fi';
import Property from './Property'; // We'll also need to update the Property component

const PropertyListings = () => {
  const containerRef = useRef(null);
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        const apiUrl = 'http://localhost/real_estate_project/api/get_all_chalets.php';
        
        const response = await fetch(apiUrl, {
          method: 'GET',
          mode: 'cors',
          headers: {
            'Accept': 'application/json'
          }
        });
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        
        const data = await response.json();
        if (Array.isArray(data)) {
          // For the homepage slider, we might want to limit to the first 5 properties
          setProperties(data.slice(0, 5));
        } else {
          throw new Error('Invalid data format received from API');
        }
      } catch (err) {
        console.error('Error fetching properties:', err);
        setError(`Error loading properties: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, []);

  const scroll = (direction) => {
    const { current } = containerRef;
    if (current) {
      const scrollAmount = 400;
      current.scrollBy({ 
        left: direction === 'left' ? -scrollAmount : scrollAmount, 
        behavior: 'smooth' 
      });
    }
  };

  if (loading) return <div className="loading">Loading properties...</div>;
  if (error) return <div className="error">{error}</div>;
  if (properties.length === 0) return <div className="no-results">No properties found.</div>;

  return (
    <section className="chalets-showcase">
      <div className="showcase-header">
        <h2 className="showcase-title">Discover the Finest Swiss Chalets</h2>
        <p className="showcase-subtitle">
          We've handpicked the most beautiful and luxurious chalets across Switzerland, ready for you to explore.
        </p>
      </div>

      <div className="chalets-container">
        <div className="chalets-carousel" ref={containerRef}>
          {properties.map(property => (
            <Property key={property.id} property={property} variant="default" />
          ))}
        </div>

        <div className="carousel-controls">
          <button onClick={() => scroll('left')} className="control-btn" aria-label="Scroll left">
            <FiChevronLeft />
          </button>
          <button onClick={() => scroll('right')} className="control-btn" aria-label="Scroll right">
            <FiChevronRight />
          </button>
        </div>
      </div>

      <div className="browse-all-container">
        <button className="browse-all-btn">
          <Link to="/properties" className="browse-all-btn">
            View All Chalets
          </Link> 
        </button>
      </div>
    </section>
  );
};

export default PropertyListings;
