// DashboardOverview.jsx - Modified to show price totals instead of other metrics
import React, { useState, useEffect } from 'react';
import { 
  FiHome, 
  FiUsers, 
  FiDollarSign, 
  FiTag,
  FiPieChart,
  FiBarChart,
  FiTrendingUp
} from 'react-icons/fi';
import axios from 'axios';
import '../styles/DashboardOverview.css';

function DashboardOverview() {
  const [stats, setStats] = useState({
    totalProperties: 0,
    totalUsers: 0,
    totalPrice: 0,
    properties: {
      available: 0,
      pending: 0,
      sold: 0
    }
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      setLoading(true);
      try {
        // Get properties data
        const propertiesResponse = await axios.get('http://localhost/real_estate_project/api/get_all_properties.php');
        
        // Get users data
        const usersResponse = await axios.get('http://localhost/real_estate_project/api/get_users.php');
        
        // Process data if valid
        if (propertiesResponse.data && usersResponse.data.success) {
          const properties = Array.isArray(propertiesResponse.data) ? propertiesResponse.data : [];
          
          // Calculate total price (sum of all property prices)
          let totalPrice = 0;
          let availableCount = 0;
          let pendingCount = 0;
          let soldCount = 0;
          
          properties.forEach(property => {
            // Add to total price
            if (property.price) {
              const price = typeof property.price === 'string' 
                ? parseFloat(property.price.replace(/[^\d.]/g, '')) 
                : parseFloat(property.price);
                
              if (!isNaN(price)) {
                totalPrice += price;
              }
            }
            
            // Count by status
            if (property.status === 'Available') availableCount++;
            if (property.status === 'Pending') pendingCount++;
            if (property.status === 'Sold') soldCount++;
          });
          
          setStats({
            totalProperties: properties.length,
            totalUsers: usersResponse.data.users.length,
            totalPrice: totalPrice,
            properties: {
              available: availableCount,
              pending: pendingCount,
              sold: soldCount
            }
          });
          
          setLoading(false);
          setError(null);
        } else {
          setError('Error retrieving dashboard data');
          setLoading(false);
        }
      } catch (error) {
        console.error('Dashboard data fetch error:', error);
        setError('Failed to load dashboard data');
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  // Format currency
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-CH', {
      style: 'currency',
      currency: 'CHF',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  return (
    <section className="dashboard-section">
      <h2 className="section-title">
        <FiPieChart className="section-icon" />
        Dashboard Overview
      </h2>
      <p className="section-subtitle">Overview of your real estate portfolio</p>
      
      {loading ? (
        <div className="loading-spinner">Loading dashboard data...</div>
      ) : error ? (
        <div className="error-message">{error}</div>
      ) : (
        <>
          <div className="stats-grid">
            <div className="stat-card">
              <FiHome className="stat-icon" />
              <div className="stat-label">Total Properties</div>
              <div className="stat-value">{stats.totalProperties}</div>
            </div>
            
            <div className="stat-card">
              <FiUsers className="stat-icon" />
              <div className="stat-label">Total Users</div>
              <div className="stat-value">{stats.totalUsers}</div>
            </div>
            
            <div className="stat-card">
              <FiDollarSign className="stat-icon" />
              <div className="stat-label">Total Property Value</div>
              <div className="stat-value">{formatCurrency(stats.totalPrice)}</div>
            </div>
            
            <div className="stat-card">
              <FiTag className="stat-icon" />
              <div className="stat-label">Available Properties</div>
              <div className="stat-value">{stats.properties.available}</div>
            </div>
          </div>
          
          <div className="charts-grid">
            <div className="chart-card">
              <h3 className="chart-title">Properties by Status</h3>
              <div className="chart-container">
                <div className="status-bars">
                  <div className="status-bar">
                    <div className="status-label">Available</div>
                    <div className="status-bar-container">
                      <div 
                        className="status-bar-fill available" 
                        style={{ width: `${(stats.properties.available / stats.totalProperties) * 100}%` }}
                      ></div>
                      <span className="status-bar-value">{stats.properties.available}</span>
                    </div>
                  </div>
                  
                  <div className="status-bar">
                    <div className="status-label">Pending</div>
                    <div className="status-bar-container">
                      <div 
                        className="status-bar-fill pending" 
                        style={{ width: `${(stats.properties.pending / stats.totalProperties) * 100}%` }}
                      ></div>
                      <span className="status-bar-value">{stats.properties.pending}</span>
                    </div>
                  </div>
                  
                  <div className="status-bar">
                    <div className="status-label">Sold</div>
                    <div className="status-bar-container">
                      <div 
                        className="status-bar-fill sold" 
                        style={{ width: `${(stats.properties.sold / stats.totalProperties) * 100}%` }}
                      ></div>
                      <span className="status-bar-value">{stats.properties.sold}</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            
            <div className="chart-card">
              <h3 className="chart-title">Property Distribution</h3>
              <div className="chart-container">
                <div className="pie-chart-container">
                  <div className="pie-chart">
                    <div 
                      className="pie-segment available" 
                      style={{ 
                        transform: `rotate(0deg)`,
                        clipPath: `polygon(50% 50%, 50% 0%, ${50 + 50 * Math.cos(2 * Math.PI * stats.properties.available / stats.totalProperties)}% ${50 - 50 * Math.sin(2 * Math.PI * stats.properties.available / stats.totalProperties)}%, 50% 50%)`
                      }}
                    ></div>
                    <div 
                      className="pie-segment pending" 
                      style={{ 
                        transform: `rotate(${360 * stats.properties.available / stats.totalProperties}deg)`,
                        clipPath: `polygon(50% 50%, 50% 0%, ${50 + 50 * Math.cos(2 * Math.PI * stats.properties.pending / stats.totalProperties)}% ${50 - 50 * Math.sin(2 * Math.PI * stats.properties.pending / stats.totalProperties)}%, 50% 50%)`
                      }}
                    ></div>
                    <div 
                      className="pie-segment sold" 
                      style={{ 
                        transform: `rotate(${360 * (stats.properties.available + stats.properties.pending) / stats.totalProperties}deg)`,
                        clipPath: `polygon(50% 50%, 50% 0%, ${50 + 50 * Math.cos(2 * Math.PI * stats.properties.sold / stats.totalProperties)}% ${50 - 50 * Math.sin(2 * Math.PI * stats.properties.sold / stats.totalProperties)}%, 50% 50%)`
                      }}
                    ></div>
                  </div>
                  <div className="pie-legend">
                    <div className="legend-item">
                      <div className="legend-color available"></div>
                      <div className="legend-label">Available ({stats.properties.available})</div>
                    </div>
                    <div className="legend-item">
                      <div className="legend-color pending"></div>
                      <div className="legend-label">Pending ({stats.properties.pending})</div>
                    </div>
                    <div className="legend-item">
                      <div className="legend-color sold"></div>
                      <div className="legend-label">Sold ({stats.properties.sold})</div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </>
      )}
    </section>
  );
}

export default DashboardOverview;
