// SearchFilter.jsx - Consistent styling with custom price input
import React, { useState, useEffect } from 'react';
import '../styles/SearchFilter.css';

const SearchFilter = ({ properties, setFilteredProperties }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [location, setLocation] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [customMinPrice, setCustomMinPrice] = useState('');
  const [customMaxPrice, setCustomMaxPrice] = useState('');
  const [minPriceToggle, setMinPriceToggle] = useState(false);
  const [maxPriceToggle, setMaxPriceToggle] = useState(false);
  const [bedrooms, setBedrooms] = useState('');
  const [bathrooms, setBathrooms] = useState('');
  const [sortBy, setSortBy] = useState('default');

  // Get unique locations for the dropdown
  const uniqueLocations = [...new Set(properties
    .map(prop => prop.location || prop.city)
    .filter(loc => loc) // Filter out null/undefined values
    .sort()
  )];
  
  // Format price as number with commas and CHF
  const formatDisplayPrice = (price) => {
    if (!price) return '';
    return new Intl.NumberFormat('fr-CH', { 
      maximumFractionDigits: 0,
      minimumFractionDigits: 0
    }).format(price);
  };

  // Parse user-entered price, removing non-numeric characters
  const parseUserPrice = (price) => {
    if (!price) return '';
    return price.replace(/[^\d]/g, '');
  };

  // Handle min price change from dropdown
  const handleMinPriceChange = (e) => {
    const value = e.target.value;
    
    if (value === 'custom') {
      setMinPriceToggle(true);
      setMinPrice('custom');
    } else {
      setMinPrice(value);
      setCustomMinPrice('');
    }
  };

  // Handle max price change from dropdown
  const handleMaxPriceChange = (e) => {
    const value = e.target.value;
    
    if (value === 'custom') {
      setMaxPriceToggle(true);
      setMaxPrice('custom');
    } else {
      setMaxPrice(value);
      setCustomMaxPrice('');
    }
  };

  // Handle custom min price input
  const handleCustomMinPriceChange = (e) => {
    const value = parseUserPrice(e.target.value);
    setCustomMinPrice(value);
  };

  // Handle custom max price input
  const handleCustomMaxPriceChange = (e) => {
    const value = parseUserPrice(e.target.value);
    setCustomMaxPrice(value);
  };

  // Toggle back to dropdown from custom input
  const toggleMinPriceDropdown = () => {
    setMinPriceToggle(false);
    if (!customMinPrice) {
      setMinPrice('');
    }
  };

  // Toggle back to dropdown from custom input
  const toggleMaxPriceDropdown = () => {
    setMaxPriceToggle(false);
    if (!customMaxPrice) {
      setMaxPrice('');
    }
  };

  const handleFilter = () => {
    let filtered = [...properties];

    // Get effective prices (either from dropdown or custom input)
    const effectiveMinPrice = customMinPrice || (minPrice !== 'custom' ? minPrice : '');
    const effectiveMaxPrice = customMaxPrice || (maxPrice !== 'custom' ? maxPrice : '');

    // Filter by search term (property name)
    if (searchTerm) {
      filtered = filtered.filter(prop => 
        prop.title && prop.title.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filter by location
    if (location) {
      filtered = filtered.filter(prop => 
        (prop.location && prop.location === location) || 
        (prop.city && prop.city === location)
      );
    }

    // Filter by price range
    if (effectiveMinPrice) {
      filtered = filtered.filter(prop => {
        const propPrice = parseFloat(prop.price);
        return !isNaN(propPrice) && propPrice >= parseFloat(effectiveMinPrice);
      });
    }
    
    if (effectiveMaxPrice) {
      filtered = filtered.filter(prop => {
        const propPrice = parseFloat(prop.price);
        return !isNaN(propPrice) && propPrice <= parseFloat(effectiveMaxPrice);
      });
    }

    // Filter by bedrooms
    if (bedrooms) {
      filtered = filtered.filter(prop => {
        const propBedrooms = parseInt(prop.bedrooms || prop.bedroom_count || prop.bed_count || prop.beds || 0);
        return propBedrooms >= parseInt(bedrooms);
      });
    }

    // Filter by bathrooms
    if (bathrooms) {
      filtered = filtered.filter(prop => {
        const propBathrooms = parseInt(prop.bathrooms || prop.bathroom_count || prop.bath_count || prop.baths || 0);
        return propBathrooms >= parseInt(bathrooms);
      });
    }

    // Sort filtered properties
    switch (sortBy) {
      case 'price-asc':
        filtered.sort((a, b) => parseFloat(a.price || 0) - parseFloat(b.price || 0));
        break;
      case 'price-desc':
        filtered.sort((a, b) => parseFloat(b.price || 0) - parseFloat(a.price || 0));
        break;
      case 'newest':
        filtered.sort((a, b) => new Date(b.created_at || 0) - new Date(a.created_at || 0));
        break;
      case 'oldest':
        filtered.sort((a, b) => new Date(a.created_at || 0) - new Date(b.created_at || 0));
        break;
      case 'most-liked':
        filtered.sort((a, b) => parseInt(b.likes || 0) - parseInt(a.likes || 0));
        break;
      case 'bedrooms-asc':
        filtered.sort((a, b) => {
          const aBedrooms = parseInt(a.bedrooms || a.bedroom_count || a.bed_count || a.beds || 0);
          const bBedrooms = parseInt(b.bedrooms || b.bedroom_count || b.bed_count || b.beds || 0);
          return aBedrooms - bBedrooms;
        });
        break;
      case 'bedrooms-desc':
        filtered.sort((a, b) => {
          const aBedrooms = parseInt(a.bedrooms || a.bedroom_count || a.bed_count || a.beds || 0);
          const bBedrooms = parseInt(b.bedrooms || b.bedroom_count || b.bed_count || b.beds || 0);
          return bBedrooms - aBedrooms;
        });
        break;
      case 'bathrooms-asc':
        filtered.sort((a, b) => {
          const aBathrooms = parseInt(a.bathrooms || a.bathroom_count || a.bath_count || a.baths || 0);
          const bBathrooms = parseInt(b.bathrooms || b.bathroom_count || b.bath_count || b.baths || 0);
          return aBathrooms - bBathrooms;
        });
        break;
      case 'bathrooms-desc':
        filtered.sort((a, b) => {
          const aBathrooms = parseInt(a.bathrooms || a.bathroom_count || a.bath_count || a.baths || 0);
          const bBathrooms = parseInt(b.bathrooms || b.bathroom_count || b.bath_count || b.baths || 0);
          return bBathrooms - aBathrooms;
        });
        break;
      case 'a-z':
        filtered.sort((a, b) => (a.title || '').localeCompare(b.title || ''));
        break;
      case 'z-a':
        filtered.sort((a, b) => (b.title || '').localeCompare(a.title || ''));
        break;
      default:
        break;
    }

    setFilteredProperties(filtered);
  };
  
  const clearFilters = () => {
    setSearchTerm('');
    setLocation('');
    setMinPrice('');
    setMaxPrice('');
    setCustomMinPrice('');
    setCustomMaxPrice('');
    setMinPriceToggle(false);
    setMaxPriceToggle(false);
    setBedrooms('');
    setBathrooms('');
    setSortBy('default');
    setFilteredProperties(properties);
  };

  return (
    <div className="property-filters">
      {/* First row - Search input */}
      <div className="filter-row">
        <input
          type="text"
          placeholder="Search chalets..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
      </div>
      
      {/* Second row - All filters in one row */}
      <div className="filter-row filters-container">
        <div className="filters-group">
          {/* Location filter */}
          <div className="filter-field">
            <select 
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              className="filter-select"
            >
              <option value="">All Locations</option>
              {uniqueLocations.map((loc, index) => (
                <option key={index} value={loc}>{loc}</option>
              ))}
            </select>
          </div>
          
          {/* Min price filter - Toggleable between select and input */}
          <div className="filter-field">
            {!minPriceToggle ? (
              <select 
                value={minPrice}
                onChange={handleMinPriceChange}
                className="filter-select"
              >
                <option value="">Min Price</option>
                <option value="500000">CHF 500,000</option>
                <option value="1000000">CHF 1,000,000</option>
                <option value="2000000">CHF 2,000,000</option>
                <option value="3000000">CHF 3,000,000</option>
                <option value="5000000">CHF 5,000,000</option>
                <option value="7000000">CHF 7,000,000</option>
                <option value="10000000">CHF 10,000,000</option>
                <option value="custom">Custom Price...</option>
              </select>
            ) : (
              <div className="price-input-wrapper">
                <input
                  type="text"
                  placeholder="Enter min price"
                  value={formatDisplayPrice(customMinPrice)}
                  onChange={handleCustomMinPriceChange}
                  className="price-input"
                />
                <button 
                  className="toggle-price-btn" 
                  onClick={toggleMinPriceDropdown}
                  title="Show preset values"
                >
                  ▼
                </button>
              </div>
            )}
          </div>
          
          {/* Max price filter - Toggleable between select and input */}
          <div className="filter-field">
            {!maxPriceToggle ? (
              <select 
                value={maxPrice}
                onChange={handleMaxPriceChange}
                className="filter-select"
              >
                <option value="">Max Price</option>
                <option value="1000000">CHF 1,000,000</option>
                <option value="2000000">CHF 2,000,000</option>
                <option value="3000000">CHF 3,000,000</option>
                <option value="5000000">CHF 5,000,000</option>
                <option value="7000000">CHF 7,000,000</option>
                <option value="10000000">CHF 10,000,000</option>
                <option value="15000000">CHF 15,000,000</option>
                <option value="20000000">CHF 20,000,000</option>
                <option value="30000000">CHF 30,000,000</option>
                <option value="custom">Custom Price...</option>
              </select>
            ) : (
              <div className="price-input-wrapper">
                <input
                  type="text"
                  placeholder="Enter max price"
                  value={formatDisplayPrice(customMaxPrice)}
                  onChange={handleCustomMaxPriceChange}
                  className="price-input"
                />
                <button 
                  className="toggle-price-btn" 
                  onClick={toggleMaxPriceDropdown}
                  title="Show preset values"
                >
                  ▼
                </button>
              </div>
            )}
          </div>
          
          {/* Bedrooms filter */}
          <div className="filter-field">
            <select 
              value={bedrooms}
              onChange={(e) => setBedrooms(e.target.value)}
              className="filter-select"
            >
              <option value="">Bedrooms</option>
              <option value="1">1+ Bedroom</option>
              <option value="2">2+ Bedrooms</option>
              <option value="3">3+ Bedrooms</option>
              <option value="4">4+ Bedrooms</option>
              <option value="5">5+ Bedrooms</option>
              <option value="6">6+ Bedrooms</option>
              <option value="7">7+ Bedrooms</option>
              <option value="8">8+ Bedrooms</option>
            </select>
          </div>
          
          {/* Bathrooms filter */}
          <div className="filter-field">
            <select 
              value={bathrooms}
              onChange={(e) => setBathrooms(e.target.value)}
              className="filter-select"
            >
              <option value="">Bathrooms</option>
              <option value="1">1+ Bathroom</option>
              <option value="2">2+ Bathrooms</option>
              <option value="3">3+ Bathrooms</option>
              <option value="4">4+ Bathrooms</option>
              <option value="5">5+ Bathrooms</option>
              <option value="6">6+ Bathrooms</option>
              <option value="7">7+ Bathrooms</option>
            </select>
          </div>
          
          {/* Sort by filter */}
          <div className="filter-field">
            <select 
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="filter-select"
            >
              <option value="default">Sort By</option>
              <option value="price-asc">Price: Low to High</option>
              <option value="price-desc">Price: High to Low</option>
              <option value="newest">Newest First</option>
              <option value="oldest">Oldest First</option>
              <option value="most-liked">Most Popular</option>
              <option value="bedrooms-asc">Bedrooms: Low to High</option>
              <option value="bedrooms-desc">Bedrooms: High to Low</option>
              <option value="bathrooms-asc">Bathrooms: Low to High</option>
              <option value="bathrooms-desc">Bathrooms: High to Low</option>
            </select>
          </div>
        </div>

        <div className="filter-buttons">
          <button onClick={handleFilter} className="filter-btn apply-btn">Apply</button>
          <button onClick={clearFilters} className="filter-btn clear-btn">Clear</button>
        </div>
      </div>
    </div>
  );
};

export default SearchFilter;
