import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Hero.css';
import { FiSearch, FiArrowRight } from 'react-icons/fi';

const Hero = () => {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  
  // Images for the carousel
  const images = [
    "/assets/Swiss_chalet_1.jpg",
    "/assets/Swiss_chalet_2.jpg",
    "/assets/Swiss_chalet_3.jpg",
    "/assets/Swiss_chalet_4.jpg",
  ];

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Function to move to next slide
  const nextSlide = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
  };

  // Function to move to previous slide
  const prevSlide = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
  };

  // Function to move to a specific slide
  const goToSlide = (index) => {
    setCurrentImageIndex(index);
  };

  // Handle search submission
  const handleSearch = (e) => {
    e.preventDefault();
    // Navigate to all properties page with search term as query parameter
    navigate(`/properties?search=${encodeURIComponent(searchTerm)}`);
  };

  // Automatic slide change
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 3000); // Change slide every 3 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <section className="hero">
      {/* Decorative elements */}
      <div className="hero-decoration hero-circle"></div>
      <div className="hero-decoration hero-dots"></div>
      
      <div className="hero-container">
        <div className="hero-content">
          <h1 className="hero-title">
            Find Your Dream Chalets in Switzerland
          </h1>
          <p className="hero-subtitle">
            Only the finest chalets, chosen for you.
            We have hand-selected the most beautiful and perfect Swiss chalets, offering an unmatched blend of luxury, comfort, and stunning natural surroundings.
          </p>
          
          {/* Search form replacing the previous button */}
          <form onSubmit={handleSearch} className="hero-search-form">
            <div className="hero-search-container">
              <FiSearch className="hero-search-icon" />
              <input
                type="text"
                placeholder="Search locations, features, or chalet names..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="hero-search-input"
              />
              <button type="submit" className="hero-btn hero-btn-primary hero-search-btn">
                Search <FiArrowRight />
              </button>
            </div>
          </form>
          
          <div className="hero-stats">
            <div className="hero-stat">
              <span className="hero-stat-number">10+</span>
              <span className="hero-stat-label">Properties</span>
            </div>
            <div className="hero-stat">
              <span className="hero-stat-number">95%</span>
              <span className="hero-stat-label">Customer Satisfaction</span>
            </div>
            <div className="hero-stat">
              <span className="hero-stat-number">15+</span>
              <span className="hero-stat-label">Years Experience</span>
            </div>
          </div>
        </div>
        
        <div className="hero-image-container">
          {/* Image slider */}
          <div className="hero-carousel">
            {images.map((image, index) => (
              <img 
                key={index}
                src={image} 
                alt={`Luxury Swiss home ${index + 1}`} 
                className={`hero-image ${index === currentImageIndex ? 'active' : ''}`}
              />
            ))}

            {/* Dots navigation */}
            <div className="carousel-dots">
              {images.map((_, index) => (
                <button 
                  key={index} 
                  className={`carousel-dot ${index === currentImageIndex ? 'active' : ''}`}
                  onClick={() => goToSlide(index)}
                  aria-label={`Go to slide ${index + 1}`}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
