import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import '../styles/ChaletDetailPage.css';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import { FaBed, FaBath, FaRulerCombined, FaCalendarAlt, FaMapMarkerAlt } from 'react-icons/fa';

const ChaletDetailPage = () => {
  const { id } = useParams();
  const [chalet, setChalet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [similarProperties, setSimilarProperties] = useState([]);
  
  useEffect(() => {
    // Fetch the specific chalet data
    fetch(`http://localhost/real_estate_project/api/get_chalet.php?id=${id}`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setChalet(data);
        setLoading(false);
        
        // After getting chalet, fetch similar properties
        return fetch(`http://localhost/real_estate_project/api/get_similar_chalets.php?id=${id}&beds=${data.beds}`);
      })
      .then(response => response.json())
      .then(similarData => {
        setSimilarProperties(similarData);
      })
      .catch(err => {
        console.error('Error fetching chalet details:', err);
        setError(err.message);
        setLoading(false);
      });
  }, [id]);

  if (loading) {
    return (
      <div className="chalet-detail-page">
        <Navbar />
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading chalet details...</p>
        </div>
        <Footer />
      </div>
    );
  }

  if (error || !chalet) {
    return (
      <div className="chalet-detail-page">
        <Navbar />
        <div className="error-container">
          <h2>Error</h2>
          <p>{error || "Chalet not found"}</p>
          <Link to="/properties" className="back-button">Back to All Properties</Link>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="chalet-detail-page">
      <Navbar />
      
      <div className="detail-container">
        <div className="breadcrumbs">
          <Link to="/">Home</Link> &gt; 
          <Link to="/properties">Properties</Link> &gt; 
          <span>{chalet.address}</span>
        </div>
        
        <div className="chalet-header">
          <h1>{chalet.address}</h1>
          <div className="chalet-price">{chalet.price}</div>
          <div className="chalet-status">{chalet.status}</div>
        </div>
        
        <div className="chalet-gallery">
          <div className="main-image">
            <img src={chalet.image} alt={`Chalet in ${chalet.address}`} />
          </div>
          {/* Additional images would go here if available */}
        </div>
        
        <div className="chalet-info-container">
          <div className="chalet-main-info">
            <div className="info-item">
              <FaBed className="info-icon" />
              <span>{chalet.beds} {chalet.beds === 1 ? 'Bedroom' : 'Bedrooms'}</span>
            </div>
            <div className="info-item">
              <FaBath className="info-icon" />
              <span>{chalet.baths} {chalet.baths === 1 ? 'Bathroom' : 'Bathrooms'}</span>
            </div>
            <div className="info-item">
              <FaRulerCombined className="info-icon" />
              <span>{chalet.sqft} sq ft</span>
            </div>
            <div className="info-item">
              <FaCalendarAlt className="info-icon" />
              <span>{chalet.days_on_market} days on market</span>
            </div>
            <div className="info-item location">
              <FaMapMarkerAlt className="info-icon" />
              <span>{chalet.address}</span>
            </div>
          </div>
          
          <div className="chalet-description">
            <h2>Property Description</h2>
            <p>{chalet.description || 'Experience the charm and luxury of this exquisite Swiss chalet. Nestled in a picturesque setting, this property offers breathtaking mountain views and premium amenities, making it the perfect retreat for those seeking both comfort and adventure in the Swiss Alps.'}</p>
          </div>
          
          <div className="chalet-features">
            <h2>Features and Amenities</h2>
            <ul className="features-list">
              <li>Panoramic mountain views</li>
              <li>Premium kitchen appliances</li>
              <li>Spacious living areas</li>
              <li>Private balcony</li>
              <li>Underfloor heating</li>
              <li>Ski storage</li>
              <li>Fireplace</li>
              <li>Parking space</li>
            </ul>
          </div>
          
          <div className="chalet-location">
            <h2>Location</h2>
            <div className="map-placeholder">
              <img 
                src={`https://maps.googleapis.com/maps/api/staticmap?center=${encodeURIComponent(chalet.address)}&zoom=15&size=600x300&markers=color:red%7C${encodeURIComponent(chalet.address)}&key=YOUR_GOOGLE_MAPS_API_KEY`} 
                alt={`Map showing ${chalet.address}`} 
              />
            </div>
          </div>
          
          <div className="contact-section">
            <h2>Interested in this property?</h2>
            <div className="contact-buttons">
              <button className="primary-button">Schedule a Viewing</button>
              <button className="secondary-button">Contact Agent</button>
            </div>
          </div>
        </div>
        
        {similarProperties.length > 0 && (
          <div className="similar-properties">
            <h2>Similar Properties</h2>
            <div className="similar-properties-grid">
              {similarProperties.map(property => (
                <Link to={`/properties/${property.id}`} key={property.id} className="similar-property-card">
                  <img src={property.image} alt={`Chalet in ${property.address}`} />
                  <div className="similar-property-details">
                    <h3>{property.address}</h3>
                    <p className="similar-property-price">{property.price}</p>
                    <div className="similar-property-info">
                      <span>{property.beds} beds</span> • 
                      <span>{property.baths} baths</span> • 
                      <span>{property.sqft} sq ft</span>
                    </div>
                  </div>
                </Link>
              ))}
            </div>
          </div>
        )}
      </div>
      
      <Footer />
    </div>
  );
};

export default ChaletDetailPage;
