import React, { useRef, useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { 
  FiMapPin, FiHome, FiLayers, FiDroplet, FiUsers, FiCalendar, 
  FiStar, FiWifi, FiCoffee, FiChevronLeft, FiChevronRight, 
  FiX, FiEye, FiPackage, FiHeart, FiFilm, FiWine, 
  FiSun, FiUser, FiBroom, FiCheck, FiShare2, FiPrinter
} from 'react-icons/fi';
import { 
  FaSnowflake, FaSkiing, FaHotTub, FaFire, FaParking,
  FaSwimmingPool, FaMountain, FaUmbrellaBeach
} from 'react-icons/fa';
import { IoIosSnow } from 'react-icons/io';
import { GiWoodCabin, GiFireplace } from 'react-icons/gi';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import '../styles/ChaletDetailPage.css';

const formatImageUrl = (imagePath) => {
  if (!imagePath) return "/placeholder.jpg";
  if (imagePath.startsWith('http://') || imagePath.startsWith('https://')) {
    return imagePath;
  }
  const cleanPath = imagePath.startsWith('/') ? imagePath.substring(1) : imagePath;
  return `http://localhost/real_estate_project/${cleanPath}`;
};

const ChaletDetailPage = () => {
  const { slug } = useParams();
  const location = useLocation();
  const propertyIdFromState = location.state?.propertyId;
  const navigate = useNavigate();
  const contactFormRef = useRef(null);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [showLightbox, setShowLightbox] = useState(false);
  const [chalet, setChalet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isFavorite, setIsFavorite] = useState(false);
  const [showShareOptions, setShowShareOptions] = useState(false);

  useEffect(() => {
    const fetchChaletData = async () => {
      try {
        setLoading(true);
        let apiUrl = propertyIdFromState 
          ? `http://localhost/real_estate_project/api/get_chalet.php?id=${propertyIdFromState}`
          : `http://localhost/real_estate_project/api/get_chalet_by_slug.php?slug=${slug}`;
        
        const response = await fetch(apiUrl, {
          method: 'GET',
          mode: 'cors',
          headers: { 'Accept': 'application/json' }
        });
        
        if (!response.ok) throw new Error(`HTTP error! Status: ${response.status}`);
        
        const data = await response.json();
        
        // Process images
        if (data.main_image) {
          data.images = [formatImageUrl(data.main_image)];
          if (data.gallery_images) {
            try {
              const gallery = JSON.parse(data.gallery_images);
              if (Array.isArray(gallery)) {
                data.images = [...data.images, ...gallery.map(img => formatImageUrl(img))];
              }
            } catch (e) {
              console.error("Error parsing gallery images:", e);
            }
          }
        } else {
          data.images = ["/placeholder.jpg"];
        }
        
        // Process amenities
        if (data.amenities) {
          try {
            data.amenities = JSON.parse(data.amenities);
          } catch (e) {
            console.error("Error parsing amenities:", e);
            data.amenities = [];
          }
        } else {
          data.amenities = [];
        }
        
        setChalet(data);
      } catch (err) {
        console.error('Error fetching chalet details:', err);
        setError(`Error loading chalet details: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchChaletData();
  }, [slug, propertyIdFromState]);

  const getIconForAmenity = (amenity) => {
    const iconMap = {
      'skiing': <FaSkiing />,
      'hot tub': <FaHotTub />,
      'fireplace': <GiFireplace />,
      'wifi': <FiWifi />,
      'parking': <FaParking />,
      'swimming pool': <FaSwimmingPool />,
      'mountain view': <FaMountain />,
      'sauna': <IoIosSnow />,
      'wood cabin': <GiWoodCabin />,
      'beach access': <FaUmbrellaBeach />
    };
    
    return iconMap[amenity.toLowerCase()] || <FiCheck />;
  };

  const scrollToContactForm = () => {
    contactFormRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const nextImage = () => {
    if (!chalet?.images?.length) return;
    setCurrentImageIndex(prev => (prev === chalet.images.length - 1 ? 0 : prev + 1));
  };

  const prevImage = () => {
    if (!chalet?.images?.length) return;
    setCurrentImageIndex(prev => (prev === 0 ? chalet.images.length - 1 : prev - 1));
  };

  const openLightbox = (index) => {
    setCurrentImageIndex(index);
    setShowLightbox(true);
  };

  const closeLightbox = () => setShowLightbox(false);

  const handleBackToListings = () => navigate('/');

  const toggleFavorite = () => setIsFavorite(!isFavorite);

  const toggleShareOptions = () => setShowShareOptions(!showShareOptions);

  const formatPrice = (price) => {
    if (!price) return 'Price on request';
    return `CHF ${Number(price).toLocaleString('de-CH')}`;
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return (
      <div className="chalet-detail-page">
        <Navbar />
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading chalet details...</p>
        </div>
        <Footer />
      </div>
    );
  }
  
  if (error || !chalet) {
    return (
      <div className="chalet-detail-page">
        <Navbar />
        <div className="error-container">
          <p>{error || 'Chalet not found.'}</p>
          <button className="back-button" onClick={handleBackToListings}>
            Back to Listings
          </button>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="chalet-detail-page">
      <Navbar />
      
      <main className="chalet-container">
        {/* Breadcrumbs */}
        <div className="breadcrumbs">
          <span onClick={() => navigate('/')}>Home</span>
          <span> / </span>
          <span onClick={() => navigate('/chalets')}>Chalets</span>
          <span> / </span>
          <span className="current">{chalet.title}</span>
        </div>
      
        {/* Image Gallery */}
        <div className="chalet-gallery">
          <div className="main-image">
            <img 
              src={chalet.images[currentImageIndex]} 
              alt={chalet.title} 
              onClick={() => openLightbox(currentImageIndex)}
              onError={(e) => e.target.src = "/placeholder.jpg"}
            />
            <button className="nav-button left" onClick={prevImage}>
              <FiChevronLeft />
            </button>
            <button className="nav-button right" onClick={nextImage}>
              <FiChevronRight />
            </button>
            
            {/* Image actions */}
            <div className="image-actions">
              <button 
                className={`favorite-btn ${isFavorite ? 'active' : ''}`}
                onClick={toggleFavorite}
              >
                <FiHeart />
              </button>
              <button className="share-btn" onClick={toggleShareOptions}>
                <FiShare2 />
              </button>
              {showShareOptions && (
                <div className="share-options">
                  <button>Copy Link</button>
                  <button>Share on Facebook</button>
                  <button>Share on Twitter</button>
                </div>
              )}
            </div>
          </div>
          
          <div className="thumbnail-grid">
            {chalet.images.slice(0, 4).map((img, index) => (
              <div 
                key={index} 
                className={`thumbnail ${index === currentImageIndex ? 'active' : ''}`}
                onClick={() => setCurrentImageIndex(index)}
              >
                <img 
                  src={img} 
                  alt={`${chalet.title} ${index + 1}`} 
                  onError={(e) => e.target.src = "/placeholder.jpg"}
                />
              </div>
            ))}
            {chalet.images.length > 4 && (
              <div className="thumbnail more-images" onClick={() => openLightbox(4)}>
                <div className="more-count">+{chalet.images.length - 4}</div>
                <img src={chalet.images[4]} alt="More images" />
              </div>
            )}
          </div>
        </div>

        {/* Lightbox Modal */}
        {showLightbox && (
          <div className="lightbox" onClick={closeLightbox}>
            <div className="lightbox-content" onClick={(e) => e.stopPropagation()}>
              <button className="close-btn" onClick={closeLightbox}>
                <FiX />
              </button>
              <img 
                src={chalet.images[currentImageIndex]} 
                alt={chalet.title}
                onError={(e) => e.target.src = "/placeholder.jpg"}
              />
              <button className="nav-button left" onClick={(e) => { e.stopPropagation(); prevImage(); }}>
                <FiChevronLeft />
              </button>
              <button className="nav-button right" onClick={(e) => { e.stopPropagation(); nextImage(); }}>
                <FiChevronRight />
              </button>
              <div className="image-counter">
                {currentImageIndex + 1} / {chalet.images.length}
              </div>
            </div>
          </div>
        )}

        {/* Main Content */}
        <div className="chalet-content">
          <div className="chalet-header">
            <div className="title-section">
              <h1>{chalet.title}</h1>
              <div className="property-id">ID: {chalet.id}</div>
            </div>
            
            <div className="location">
              <FiMapPin /> 
              <span>{chalet.address}, {chalet.postal_code} {chalet.city}, {chalet.canton}</span>
            </div>
            
            <div className="property-meta">
              <div className="meta-item">
                <FiCalendar /> Listed: {formatDate(chalet.created_at)}
              </div>
              <div className="meta-item">
                <FiEye /> Views: {chalet.views || '0'}
              </div>
              <div className="meta-item">
                <FiPackage /> Status: <span className="status">{chalet.status}</span>
              </div>
            </div>
          </div>

          {/* Price and Quick Facts */}
          <div className="price-section">
            <div className="price-display">
              <div className="main-price">{formatPrice(chalet.price)}</div>
              {chalet.price && (
                <div className="price-details">
                  <span>CHF {Math.round(chalet.price / chalet.sqft).toLocaleString()} per sqft</span>
                </div>
              )}
            </div>
            
            <div className="quick-facts">
              <div className="fact">
                <FiHome /> {chalet.beds} beds
              </div>
              <div className="fact">
                <FiDroplet /> {chalet.baths} baths
              </div>
              <div className="fact">
                <FiLayers /> {chalet.sqft?.toLocaleString() || 'N/A'} sqft
              </div>
              <div className="fact">
                <FiCalendar /> Built: {chalet.year_built || 'N/A'}
              </div>
              <div className="fact">
                <FiUsers /> Type: {chalet.property_type || 'Chalet'}
              </div>
            </div>
          </div>

          {/* Description */}
          <div className="description-section">
            <h2>Property Description</h2>
            <p>{chalet.description || 'No description available.'}</p>
          </div>

          {/* Property Details */}
          <div className="details-section">
            <h2>Property Details</h2>
            <div className="details-grid">
              <div className="detail-item">
                <span className="detail-label">Property ID:</span>
                <span className="detail-value">{chalet.id}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Price:</span>
                <span className="detail-value">{formatPrice(chalet.price)}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Property Size:</span>
                <span className="detail-value">{chalet.sqft?.toLocaleString() || 'N/A'} sqft</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Bedrooms:</span>
                <span className="detail-value">{chalet.beds}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Bathrooms:</span>
                <span className="detail-value">{chalet.baths}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Year Built:</span>
                <span className="detail-value">{chalet.year_built || 'N/A'}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Property Type:</span>
                <span className="detail-value">{chalet.property_type || 'Chalet'}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Status:</span>
                <span className="detail-value status">{chalet.status}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Days on Market:</span>
                <span className="detail-value">{chalet.days_on_market || '0'}</span>
              </div>
            </div>
          </div>

          {/* Amenities */}
          {chalet.amenities.length > 0 && (
            <div className="amenities-section">
              <h2>Amenities</h2>
              <div className="amenities-grid">
                {chalet.amenities.map((amenity, index) => (
                  <div key={index} className="amenity-item">
                    <span className="amenity-icon">{getIconForAmenity(amenity)}</span>
                    <span>{amenity}</span>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Location */}
          <div className="location-section" ref={contactFormRef}>
            <h2>Location</h2>
            <div className="address">
              <FiMapPin /> {chalet.address}, {chalet.postal_code} {chalet.city}, {chalet.canton}
            </div>
            
            {chalet.latitude && chalet.longitude ? (
              <div className="map-container">
                <div className="map-placeholder">
                  {/* In a real app, you would embed a map here */}
                  <p>Map would display here with coordinates: {chalet.latitude}, {chalet.longitude}</p>
                </div>
              </div>
            ) : (
              <p>Location details not available</p>
            )}
            
            <div className="neighborhood-info">
              <h3>About {chalet.city}</h3>
              <p>Explore the beautiful surroundings of this {chalet.property_type || 'chalet'} located in the heart of {chalet.canton}.</p>
            </div>
          </div>

          {/* Contact Form */}
          <div className="contact-section">
            <h2>Request More Information</h2>
            <form className="contact-form">
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="name">Name</label>
                  <input type="text" id="name" required />
                </div>
                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <input type="email" id="email" required />
                </div>
              </div>
              
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="phone">Phone</label>
                  <input type="tel" id="phone" />
                </div>
                <div className="form-group">
                  <label htmlFor="subject">Subject</label>
                  <input 
                    type="text" 
                    id="subject" 
                    value={`Inquiry about ${chalet.title}`} 
                    readOnly 
                  />
                </div>
              </div>
              
              <div className="form-group">
                <label htmlFor="message">Message</label>
                <textarea 
                  id="message" 
                  rows="5" 
                  required
                  placeholder={`I'm interested in ${chalet.title} at ${chalet.address}. Please provide more information about...`}
                ></textarea>
              </div>
              
              <div className="form-actions">
                <button type="submit" className="submit-btn">
                  Send Inquiry
                </button>
                <button type="button" className="print-btn">
                  <FiPrinter /> Print Details
                </button>
              </div>
            </form>
          </div>
        </div>

        {/* Booking Sidebar */}
        <aside className="booking-sidebar">
          <div className="booking-card">
            <div className="card-header">
              <h3>Contact Agent</h3>
              <div className="price">{formatPrice(chalet.price)}</div>
            </div>
            
            <div className="agent-info">
              <div className="agent-avatar">
                <FiUser />
              </div>
              <div className="agent-details">
                <h4>Property Agent</h4>
                <p>Swiss Real Estate</p>
              </div>
            </div>
            
            <div className="contact-methods">
              <button className="contact-btn whatsapp">
                WhatsApp
              </button>
              <button className="contact-btn phone">
                Call Now
              </button>
              <button 
                className="contact-btn email"
                onClick={scrollToContactForm}
              >
                Email Agent
              </button>
            </div>
            
            <div className="property-summary">
              <h4>Property Summary</h4>
              <div className="summary-item">
                <span>Type:</span>
                <span>{chalet.property_type || 'Chalet'}</span>
              </div>
              <div className="summary-item">
                <span>Bedrooms:</span>
                <span>{chalet.beds}</span>
              </div>
              <div className="summary-item">
                <span>Bathrooms:</span>
                <span>{chalet.baths}</span>
              </div>
              <div className="summary-item">
                <span>Size:</span>
                <span>{chalet.sqft?.toLocaleString() || 'N/A'} sqft</span>
              </div>
              <div className="summary-item">
                <span>Year Built:</span>
                <span>{chalet.year_built || 'N/A'}</span>
              </div>
              <div className="summary-item">
                <span>Status:</span>
                <span className="status">{chalet.status}</span>
              </div>
            </div>
            
            <div className="schedule-tour">
              <h4>Schedule a Tour</h4>
              <button className="tour-btn">
                Book a Viewing
              </button>
            </div>
          </div>
          
          <div className="download-section">
            <h4>Download Property Details</h4>
            <button className="download-btn">
              Download PDF Brochure
            </button>
          </div>
        </aside>
      </main>
      
      <Footer />
    </div>
  );
};

export default ChaletDetailPage;
