import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FiHome, FiSearch, FiMail, FiInfo, FiLogIn, FiUser, FiSettings, FiLogOut } from 'react-icons/fi';
import { useAuth } from '../contexts/AuthContext';
import '../styles/Navbar.css';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const location = useLocation();
  const { user, logout, isAuthenticated, syncSession } = useAuth();

  // Set active link based on current path
  const getActiveLink = () => {
    const path = location.pathname;
    if (path === '/' || path === '/home') return 'home';
    if (path === '/properties') return 'buy';
    if (path === '/rent') return 'rent';
    if (path === '/about') return 'about';
    if (path === '/contact') return 'contact';
    return '';
  };

  const activeLink = getActiveLink();

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 10);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Sync session when navbar mounts or when authentication status changes
  useEffect(() => {
    syncSession();
  }, [location.pathname]); // Sync on route changes

  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);
  const closeMenu = () => setIsMenuOpen(false);

  const handleHomeClick = () => {
    closeMenu();
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = async () => {
    await logout();
    setIsDropdownOpen(false);
  };

  // Click outside handlers
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (isMenuOpen && 
          !event.target.closest('.navbar-links') && 
          !event.target.closest('.menu-toggle')) {
        setIsMenuOpen(false);
      }
      
      if (isDropdownOpen && 
          !event.target.closest('.navbar-user')) {
        setIsDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [isMenuOpen, isDropdownOpen]);

  // Handle body overflow
  useEffect(() => {
    if (isMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'auto';
    }
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, [isMenuOpen]);

  // Get username from email or name
  const getUserName = () => {
    if (!user) return '';
    
    // Use name if available, otherwise extract from email
    if (user.name) {
      return user.name;
    }
    
    if (user.email) {
      const emailParts = user.email.split('@');
      return emailParts[0];
    }
    
    return 'User';
  };

  return (
    <nav className={`navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="navbar-content">
        <div className="navbar-logo">
          <Link to="/" onClick={handleHomeClick}>
            <span className="navbar-logo-icon"></span>
            Chalets Suisses
          </Link>
        </div>

        <div 
          className={`menu-toggle ${isMenuOpen ? 'active' : ''}`} 
          onClick={toggleMenu}
          aria-label="Toggle menu"
        >
          <span></span>
          <span></span>
          <span></span>
        </div>

        <ul className={`navbar-links ${isMenuOpen ? 'active' : ''}`}>
          <li>
            <Link 
              to="/" 
              className={activeLink === 'home' ? 'active' : ''} 
              onClick={handleHomeClick}
            >
              <FiHome /> Home
            </Link>
          </li>
          <li>
            <Link 
              to="/properties" 
              className={activeLink === 'buy' ? 'active' : ''} 
              onClick={closeMenu}
            >
              <FiSearch /> Buy Chalets         
            </Link>
          </li>
          <li>
            <Link 
              to="/coming-soon" 
              onClick={closeMenu}
            >
              <FiSearch /> Price Estimate
            </Link>
          </li>
          <li>
            <Link 
              to="/contact" 
              className={activeLink === 'contact' ? 'active' : ''} 
              onClick={closeMenu}
            >
              <FiMail /> Contact Us
            </Link>
          </li>

          {isAuthenticated ? (
            <div className="navbar-user" onClick={toggleDropdown}>
              <FiUser />
              <span className="username">{getUserName()}</span>
              {isDropdownOpen && (
                <div className="dropdown-menu">
                  {user && user.is_admin ? (
                    <Link to="/admin-dashboard" onClick={() => setIsDropdownOpen(false)}>
                      <FiSettings /> Admin Dashboard
                    </Link>
                  ) : (
                    <Link to="/user-dashboard" onClick={() => setIsDropdownOpen(false)}>
                      <FiSettings /> My Dashboard
                    </Link>
                  )}
                  <button onClick={handleLogout}>
                    <FiLogOut /> Log out
                  </button>
                </div>
              )}
            </div>
          ) : (
            <div className="navbar-auth">
              <Link to="/login" className="login-btn" onClick={closeMenu}>
                <FiLogIn /> Login / Sign Up
              </Link>
            </div>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
