import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import '../styles/Login.css'; // Using the same animated styles

function Register() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const { login } = useAuth();

  // Initialize Google Sign-In
  useEffect(() => {
    const initializeGoogleSignIn = () => {
      if (window.google && window.google.accounts) {
        try {
          window.google.accounts.id.initialize({
            client_id: process.env.REACT_APP_GOOGLE_CLIENT_ID,
            callback: handleGoogleResponse,
            auto_select: false,
            cancel_on_tap_outside: true,
          });
          
          console.log('Google Sign-In initialized successfully for registration');
        } catch (error) {
          console.error('Google Sign-In initialization error:', error);
          setMessage('Failed to initialize Google Sign-In');
          setMessageType('error');
        }
      }
    };

    // Load Google Sign-In script if not already loaded
    if (!window.google) {
      const script = document.createElement('script');
      script.src = 'https://accounts.google.com/gsi/client';
      script.async = true;
      script.defer = true;
      script.onload = () => {
        console.log('Google Sign-In script loaded');
        initializeGoogleSignIn();
      };
      script.onerror = () => {
        console.error('Failed to load Google Sign-In script');
        setMessage('Failed to load Google Sign-In');
        setMessageType('error');
      };
      document.head.appendChild(script);
    } else {
      initializeGoogleSignIn();
    }
  }, []);

  const handleGoogleResponse = async (response) => {
    try {
      setLoading(true);
      setMessage('');

      console.log('Google response received:', response);

      if (!response.credential) {
        setMessage('No credential received from Google');
        setMessageType('error');
        return;
      }

      // Use environment variable for API base URL
      const result = await fetch(`${process.env.REACT_APP_API_BASE_URL}/google-register.php`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify({
          token: response.credential
        })
      });

      if (!result.ok) {
        throw new Error(`HTTP error! status: ${result.status}`);
      }

      const data = await result.json();
      console.log('Server response:', data);
      
      if (data.success) {
        if (data.new_user) {
          setMessage('Account created successfully with Google!');
          setMessageType('success');
          
          // Auto-login the user
          login({
            id: data.user_id, 
            email: data.email,
            is_admin: data.is_admin,
            name: data.name 
          });
        } else {
          setMessage('Account already exists. Logging you in...');
          setMessageType('success');
          
          // Auto-login existing user
          login({
            id: data.user_id, 
            email: data.email,
            is_admin: data.is_admin,
            name: data.name 
          });
        }
      } else {
        setMessage(data.message || 'Google registration failed');
        setMessageType('error');
      }
    } catch (error) {
      console.error('Google registration error:', error);
      setMessage('Google registration failed. Please try again.');
      setMessageType('error');
    } finally {
      setLoading(false);
    }
  };

  // Custom Google registration function that opens in popup
  const handleCustomGoogleRegister = () => {
    if (window.google && window.google.accounts && window.google.accounts.oauth2) {
      try {
        setLoading(true);
        setMessage('');
        
        // Use OAuth2 popup flow
        const client = window.google.accounts.oauth2.initTokenClient({
          client_id: process.env.REACT_APP_GOOGLE_CLIENT_ID,
          scope: 'email profile',
          ux_mode: 'popup',
          callback: async (response) => {
            if (response.access_token) {
              await handleGoogleTokenResponse(response.access_token);
            } else if (response.error) {
              console.error('Google OAuth error:', response.error);
              setMessage('Google sign-up was cancelled or failed');
              setMessageType('error');
              setLoading(false);
            }
          },
        });
        
        client.requestAccessToken();
      } catch (error) {
        console.error('Google OAuth error:', error);
        setMessage('Failed to initialize Google sign-up');
        setMessageType('error');
        setLoading(false);
      }
    } else {
      setMessage('Google Sign-In not loaded');
      setMessageType('error');
    }
  };

  // Handle the access token from Google OAuth popup
  const handleGoogleTokenResponse = async (accessToken) => {
    try {
      // Get user info from Google using the access token
      const userInfoResponse = await fetch(`https://www.googleapis.com/oauth2/v2/userinfo?access_token=${accessToken}`);
      const userInfo = await userInfoResponse.json();
      
      if (!userInfo.email) {
        setMessage('Failed to get user information from Google');
        setMessageType('error');
        return;
      }

      // Send user info to your backend
      const result = await fetch(`${process.env.REACT_APP_API_BASE_URL}/google-oauth-register.php`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify({
          email: userInfo.email,
          name: userInfo.name,
          google_id: userInfo.id,
          picture: userInfo.picture
        })
      });

      if (!result.ok) {
        throw new Error(`HTTP error! status: ${result.status}`);
      }

      const data = await result.json();
      console.log('Server response:', data);
      
      if (data.success) {
        if (data.new_user) {
          setMessage('Account created successfully with Google!');
          setMessageType('success');
          
          // Auto-login the user
          login({
            id: data.user_id, 
            email: data.email,
            is_admin: data.is_admin,
            name: data.name 
          });
        } else {
          setMessage('Account already exists. Logging you in...');
          setMessageType('success');
          
          // Auto-login existing user
          login({
            id: data.user_id, 
            email: data.email,
            is_admin: data.is_admin,
            name: data.name 
          });
        }
      } else {
        setMessage(data.message || 'Google registration failed');
        setMessageType('error');
      }
    } catch (error) {
      console.error('Google token processing error:', error);
      setMessage('Google registration failed. Please try again.');
      setMessageType('error');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    
    // Basic validation
    if (formData.password !== formData.confirmPassword) {
      setMessage("Passwords don't match!");
      setMessageType('error');
      return;
    }

    if (formData.password.length < 6) {
      setMessage("Password must be at least 6 characters long!");
      setMessageType('error');
      return;
    }

    setLoading(true);
    setMessage('');

    try {
      const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/register.php`, {
        method: "POST",
        headers: { 
          "Content-Type": "application/json" 
        },
        credentials: 'include',
        body: JSON.stringify({ 
          email: formData.email, 
          password: formData.password 
        }),
      });

      const data = await response.json();
      
      if (data.success || data.message.includes("success")) {
        setMessage(data.message || "Registration successful!");
        setMessageType('success');
        
        // Redirect to login after successful registration
        setTimeout(() => {
          navigate('/login');
        }, 2000);
      } else {
        setMessage(data.message || "Registration failed.");
        setMessageType('error');
      }
    } catch (error) {
      setMessage("Network error. Please try again.");
      setMessageType('error');
      console.error('Registration error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      {/* Animated Background Shapes */}
      <div className="shape1"></div>
      <div className="shape2"></div>
      
      <div className="login-form-container">
        <div className="login-header">
          <h1>
            <span className="gradient-text">Create Account</span>
          </h1>
          <p>Sign up to get started</p>
        </div>
        
        {message && (
          <div className={`${messageType === 'success' ? 'success-message' : 'error-message'}`}>
            {message}
          </div>
        )}
        
        {/* Custom Google Sign-Up Button */}
        <div className="google-signin-container">
          <button 
            type="button" 
            onClick={handleCustomGoogleRegister}
            className="google-signin-button"
            disabled={loading}
          >
            <svg width="18" height="18" viewBox="0 0 18 18" style={{ marginRight: '8px' }}>
              <path fill="#4285F4" d="M16.51 8H8.98v3h4.3c-.18 1-.74 1.48-1.6 2.04v2.01h2.6a7.8 7.8 0 0 0 2.38-5.88c0-.57-.05-.66-.15-1.18z"/>
              <path fill="#34A853" d="M8.98 17c2.16 0 3.97-.72 5.3-1.94l-2.6-2.04a4.8 4.8 0 0 1-7.18-2.53H1.83v2.07A8 8 0 0 0 8.98 17z"/>
              <path fill="#FBBC05" d="M4.5 10.49a4.8 4.8 0 0 1 0-3.07V5.35H1.83a8 8 0 0 0 0 7.28l2.67-2.14z"/>
              <path fill="#EA4335" d="M8.98 3.54c1.17 0 2.23.4 3.06 1.2l2.3-2.3A8 8 0 0 0 1.83 5.35L4.5 7.42a4.77 4.77 0 0 1 4.48-3.88z"/>
            </svg>
            {loading ? 'Creating account...' : 'Sign up with Google'}
          </button>
        </div>
        
        <div className="divider">
          <span>or</span>
        </div>
        
        <form onSubmit={handleRegister} className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Enter your email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Enter your password"
              value={formData.password}
              onChange={handleChange}
              required
              minLength="6"
            />
          </div>

          <div className="form-group">
            <label htmlFor="confirmPassword">Confirm Password</label>
            <input
              type="password"
              id="confirmPassword"
              name="confirmPassword"
              placeholder="Confirm your password"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
              minLength="6"
            />
          </div>
          
          <button 
            type="submit" 
            className={`login-button ${loading ? 'loading' : ''}`} 
            disabled={loading}
          >
            {loading ? 'Creating account...' : 'Create Account'}
          </button>
        </form>
        
        <div className="login-footer">
          <p>
            Already have an account? <Link to="/login">Log in</Link>
          </p>
          <p>
            <Link to="/">Return to Home</Link>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Register;
