import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Login.css';

const Login = () => {
  const [email, setEmail] = useState('admin@test.com');
  const [password, setPassword] = useState('password');
  const [error, setError] = useState('');
  const [errorDetails, setErrorDetails] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setErrorDetails(null);
    setIsLoading(true);
    
    try {
      console.log('Attempting login with:', { email, password });
      
      const response = await fetch(
        'http://localhost/real_estate_project/api/login.php',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, password }),
          credentials: 'include'
        }
      );

      console.log('Response status:', response.status);
      
      // Get full response text
      const responseText = await response.text();
      console.log('Raw response:', responseText);
      
      // Parse JSON if possible
      let data;
      try {
        data = JSON.parse(responseText);
        console.log('Parsed response:', data);
      } catch (parseError) {
        console.error('JSON parse error:', parseError);
        setError('Server returned invalid JSON response');
        setErrorDetails({
          message: 'Response was not valid JSON',
          raw: responseText
        });
        return;
      }

      if (data.success) {
        // Store auth data in localStorage for client-side checks
        localStorage.setItem('isAuthenticated', 'true');
        localStorage.setItem('isAdmin', data.is_admin ? 'true' : 'false');
        localStorage.setItem('userId', data.user_id);
        
        // Redirect based on user type
        navigate(data.is_admin ? '/dashboard' : '/');
      } else {
        setError(data.error || 'Invalid credentials');
        if (data.error_details) {
          setErrorDetails(data.error_details);
        }
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('Connection error. Please try again.');
      setErrorDetails({
        message: err.message,
        stack: err.stack
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h1>Login to Your Account</h1>
        
        {error && (
          <div className="error-message">
            <p><strong>Error:</strong> {error}</p>
            {errorDetails && (
              <details>
                <summary>Details (click to expand)</summary>
                <pre className="error-details">
                  {JSON.stringify(errorDetails, null, 2)}
                </pre>
              </details>
            )}
          </div>
        )}
        
        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <input
              id="email"
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              id="password"
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          
          <button 
            type="submit" 
            className="login-button"
            disabled={isLoading}
          >
            {isLoading ? 'Logging in...' : 'Login'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
