import React, { useState, useEffect } from 'react';
import { FiEdit, FiTrash2, FiPlus, FiX, FiSave, FiArrowLeft } from 'react-icons/fi';

function UserManagement() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    is_admin: 0
  });
  const [formErrors, setFormErrors] = useState({});

  // Fetch users from the API
  useEffect(() => {
    fetchUsers();
  }, []);

  // When editing user changes, update form data
  useEffect(() => {
    if (currentUser) {
      setFormData({
        name: currentUser.name || '',
        email: currentUser.email || '',
        password: '', // Don't display the password
        is_admin: currentUser.is_admin || 0
      });
    } else if (isEditing) {
      // Reset form for new user
      setFormData({
        name: '',
        email: '',
        password: '',
        is_admin: 0
      });
    }
  }, [currentUser, isEditing]);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost/REAL_ESTATE_PROJECT/api/get_users.php', {
        credentials: 'include',
      });
      
      if (!response.ok) {
        throw new Error('Failed to fetch users');
      }
      
      const data = await response.json();
      
      if (data.success) {
        setUsers(data.users || []);
      } else {
        setError(data.message || 'Failed to fetch users');
      }
    } catch (error) {
      console.error('Error fetching users:', error);
      setError('Error fetching users. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleEditClick = (userId) => {
    const user = users.find(user => user.id === userId);
    if (user) {
      setCurrentUser(user);
      setIsEditing(true);
    }
  };

  const handleAddClick = () => {
    setCurrentUser(null);
    setIsEditing(true);
    setFormData({
      name: '',
      email: '',
      password: '',
      is_admin: 0
    });
  };

  const handleDeleteUser = async (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        const response = await fetch('http://localhost/REAL_ESTATE_PROJECT/api/delete_user.php', {
          method: 'POST',
          credentials: 'include',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ userId }),
        });
        
        const data = await response.json();
        
        if (data.success) {
          // Remove user from state
          setUsers(users.filter(user => user.id !== userId));
          alert('User deleted successfully');
        } else {
          alert(data.message || 'Failed to delete user');
        }
      } catch (error) {
        console.error('Error deleting user:', error);
        alert('Error deleting user. Please try again later.');
      }
    }
  };

  const handleFormChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? (checked ? 1 : 0) : value
    });
  };

  const validateForm = () => {
    const errors = {};
    
    if (!formData.name.trim()) {
      errors.name = 'Name is required';
    }
    
    if (!formData.email.trim()) {
      errors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = 'Email is invalid';
    }
    
    // Only validate password for new users
    if (!currentUser && !formData.password) {
      errors.password = 'Password is required for new users';
    }
    
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSaveUser = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    const userData = {
      ...formData,
      id: currentUser ? currentUser.id : null
    };
    
    try {
      const response = await fetch('http://localhost/REAL_ESTATE_PROJECT/api/save_user.php', {
        method: 'POST',
        credentials: 'include',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userData),
      });
      
      const data = await response.json();
      
      if (data.success) {
        if (currentUser) {
          // Update existing user in state
          setUsers(users.map(user => 
            user.id === currentUser.id ? data.user : user
          ));
        } else {
          // Add new user to state
          setUsers([data.user, ...users]);
        }
        
        setIsEditing(false);
        setCurrentUser(null);
        alert(data.message);
      } else {
        alert(data.message || 'Failed to save user');
      }
    } catch (error) {
      console.error('Error saving user:', error);
      alert('Error saving user. Please try again later.');
    }
  };

  const handleCancelEdit = () => {
    setIsEditing(false);
    setCurrentUser(null);
    setFormErrors({});
  };

  return (
    <section id="users" className="dashboard-section">
      <div className="section-header">
        <h2 className="section-title">User Management</h2>
        <p className="section-subtitle">Manage all registered users</p>
      </div>
      
      {!isEditing ? (
        <div>
          <button className="add-chalet-btn" onClick={handleAddClick}>
            <FiPlus /> Add New User
          </button>
          
          <div className="section-content">
            {loading ? (
              <div className="loading-spinner">Loading users...</div>
            ) : error ? (
              <div className="error-message">{error}</div>
            ) : users.length === 0 ? (
              <div className="no-results">No users found.</div>
            ) : (
              <div className="table-container">
                <table className="data-table">
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>Name</th>
                      <th>Email</th>
                      <th>Role</th>
                      <th>Status</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {users.map(user => (
                      <tr key={user.id}>
                        <td>{user.id}</td>
                        <td>{user.name}</td>
                        <td>{user.email}</td>
                        <td>{user.is_admin == 1 ? 'Admin' : 'User'}</td>
                        <td>
                          <span className="status active">Active</span>
                        </td>
                        <td>
                          <div className="action-buttons">
                            <button 
                              className="edit-btn"
                              onClick={() => handleEditClick(user.id)}
                              title="Edit User"
                            >
                              <FiEdit />
                            </button>
                            <button 
                              className="delete-btn"
                              onClick={() => handleDeleteUser(user.id)}
                              title="Delete User"
                            >
                              <FiTrash2 />
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>
      ) : (
        <div className="user-form-container">
          <div className="form-header">
            <h3>
              {currentUser ? `Edit User: ${currentUser.name}` : 'Add New User'}
            </h3>
            <button className="back-btn" onClick={handleCancelEdit}>
              <FiArrowLeft /> Back to Users
            </button>
          </div>
          
          <form onSubmit={handleSaveUser} className="user-form">
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleFormChange}
                className={formErrors.name ? 'error' : ''}
              />
              {formErrors.name && <div className="error-text">{formErrors.name}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleFormChange}
                className={formErrors.email ? 'error' : ''}
              />
              {formErrors.email && <div className="error-text">{formErrors.email}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="password">
                {currentUser ? 'Password (leave empty to keep current)' : 'Password'}
              </label>
              <input
                type="password"
                id="password"
                name="password"
                value={formData.password}
                onChange={handleFormChange}
                className={formErrors.password ? 'error' : ''}
              />
              {formErrors.password && <div className="error-text">{formErrors.password}</div>}
            </div>
            
            <div className="form-group checkbox-group">
              <input
                type="checkbox"
                id="is_admin"
                name="is_admin"
                checked={formData.is_admin == 1}
                onChange={handleFormChange}
              />
              <label htmlFor="is_admin">Admin User</label>
            </div>
            
            <div className="form-actions">
              <button type="submit" className="save-btn">
                <FiSave /> Save User
              </button>
              <button type="button" className="cancel-btn" onClick={handleCancelEdit}>
                <FiX /> Cancel
              </button>
            </div>
          </form>
        </div>
      )}
    </section>
  );
}

export default UserManagement;
